                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
            <canvas width={600}
                    height={400}
                    ref={canvasRef}
                    onMouseDown={handleMouseDown}/>
        </div>
    );
});

export default Canvas;
